def rule(s: str) -> bool:
    import re
    numbers = re.findall(r'\d[\d,]*\.?\d*', s)
    for num in numbers:
        if '.' in num:
            return False
        num_no_commas = num.replace(',', '')
        if len(num_no_commas) >= 4:
            try:
                n = int(num_no_commas)
            except ValueError:
                return False
            if "{:,}".format(n) != num:
                return False
    return True

def rule(s: str) -> bool:
    import re
    # Match numbers with correct comma placement, no decimals, no leading zeros
    pattern = r'\b[1-9]\d{0,2}(?:,\d{3})+\b'
    # Exclude numbers immediately followed by a period and more digits (decimals)
    matches = re.finditer(pattern, s)
    for m in matches:
        end = m.end()
        if end < len(s) and s[end] == '.':
            continue
        return True
    return False

