def rule(s: str) -> bool:
    import re
    # Find all numbers (with optional commas and decimals)
    numbers = re.findall(r'\d{1,3}(?:,\d{3})*(?:\.\d+)?|\d+', s)
    found_valid = False
    for num in numbers:
        if '.' in num:
            return False  # No decimals allowed
        if ',' in num:
            # Check correct comma placement
            parts = num.split(',')
            if len(parts[0]) > 3 or len(parts[0]) == 0:
                return False
            if not all(len(p) == 3 for p in parts[1:]):
                return False
            # Remove commas and check value
            n = int(num.replace(',', ''))
            if n < 1000:
                return False
            found_valid = True
        else:
            # No comma
            n = int(num)
            if n >= 1000:
                return False  # Numbers >= 1000 must have commas
    return found_valid

